import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { OauthSender } from 'react-oauth-flow';
import login2 from '../api/api';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {html: ''};
  }

  handleLogin = () => {
    let htmlString = login2();
    this.setState((state) => {
      return {html: htmlString}
    });

  }

  render () {
    return (
      <div id="login">
        <OauthSender
          authorizeUrl={'http://35.246.165.105:9001/authorizationserver/oauth/authorize'}
          clientId={'client-side'}
          redirectUri={'http://35.246.165.105:9001/authorizationserver/oauth2_implicit_callback'}
          render={({ url }) => <a href={url}>Connect to Dropbox</a>}
          args={{'scope': 'basic'}}
        />
        <button onClick={this.handleLogin}>Login</button>
        <div id="login-form">
        </div>
      </div>
    );
  }
}

export default Login;
