import React, {Component} from 'react';
import Cart from './cart';
import ProductList from './productList';

class Shop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cart:0
    };

  }
  handleAddToCart = () => {
    this.setState(state => ({
      cart: state.cart + 1
    }));
  }
  render () {
    return (
      <div>
        <Cart cart={this.state.cart}/>
        <ProductList cart={this.state.cart}/>
      </div>
    );
  }
}
