  let implicit = {
    grant_type: 'implicit',
    url: 'http://35.246.165.105:9001/authorizationserver/oauth/authorize',
    redirect_uri: 'http://35.246.165.105:9001/authorizationserver/oauth2_implicit_callback',
    client_id: 'client-side',
    scope: 'basic',
    state: '$wCAVZ49kCOn.slStMc;Lva',
    redirect: 'follow',
  }
